CREATE TABLE IF NOT EXISTS sms_message_history (
  id SERIAL PRIMARY KEY,
  user_id INTEGER NOT NULL REFERENCES users(id) ON DELETE CASCADE,
  phone_number VARCHAR(20) NOT NULL,
  message_type VARCHAR(50) NOT NULL,
  message_content TEXT NOT NULL,
  status VARCHAR(20) NOT NULL DEFAULT 'pending',
  twilio_message_sid VARCHAR(100),
  error_message TEXT,
  sent_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  delivered_at TIMESTAMP,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE INDEX IF NOT EXISTS idx_sms_history_user_id ON sms_message_history(user_id);
CREATE INDEX IF NOT EXISTS idx_sms_history_sent_at ON sms_message_history(sent_at DESC);
CREATE INDEX IF NOT EXISTS idx_sms_history_status ON sms_message_history(status);
CREATE INDEX IF NOT EXISTS idx_sms_history_message_type ON sms_message_history(message_type);

